mapper.select:
select IFNULL(sum(product_price * amount), 0) money
from tbl_cart c, tbl_product p
where c.product_id = p.product_id and c.userid = #{userid}

select count(*)
from tbl_cart
where userid = #{userid} and product_id = #{product_id}

mapper.update:
update tbl_cart
set amount = amount + #{amount}
where userid = #{userid} and product_id = #{product_id}
