mapper.select:

select *
from
(
    select /*+ INDEX(tbl_review SYS_C0015606) */
        rownum rn, review_no, product_id, review_title, review_content, review_date, review_updateDate, userid, review_star
        from tbl_review
        where product_id = #{product_id}
        and review_no > 0
        and rownum <= #{cri.pageNum} * #{cri.amount}
) where rn > (#{cri.pageNum} -1) * #{cri.amount}
